mapper.select:
SELECT count(*) FROM pddou_order o JOIN pddou_user u ON o.user_id=u.id JOIN pddou_schedule s ON o.schedule_id=s.id JOIN pddou_movie m ON s.movie_id=m.id JOIN pddou_cinema c ON s.cinema_id=c.id JOIN pddou_movie_cinema mc ON s.movie_id=mc.movie_id AND s.cinema_id=mc.cinema_id
c.id=#{cinemaId}
AND o.order_num=#{orderNum}
AND o.pay_type=#{payType}
AND o.is_delete=0
AND u.username LIKE concat('%',#{username},'%')
AND m.`name` LIKE concat('%',#{movieName},'%')
AND c.cinema_name LIKE concat('%',#{cinemaName},'%')

SELECT o.id, o.order_num, u.username, m.`name` AS movie_name, c.cinema_name, s.hall_name, o.order_date, o.ticket_num, o.ticket_total_price, o.ticket_code, o.pay_type, s.show_date, s.show_time FROM pddou_order o JOIN pddou_user u ON o.user_id=u.id JOIN pddou_schedule s ON o.schedule_id=s.id JOIN pddou_movie m ON s.movie_id=m.id JOIN pddou_cinema c ON s.cinema_id=c.id JOIN pddou_movie_cinema mc ON s.movie_id=mc.movie_id AND s.cinema_id=mc.cinema_id
c.id=#{cinemaId}
AND o.order_num=#{orderNum}
AND o.pay_type=#{payType}
AND o.is_delete=0
AND u.username LIKE concat('%',#{username},'%')
AND m.`name` LIKE concat('%',#{movieName},'%')
AND c.cinema_name LIKE concat('%',#{cinemaName},'%')
ORDER BY o.order_date DESC LIMIT #{page}, #{limit}
