mapper.select:
SELECT count(*) FROM pddou_schedule s JOIN pddou_movie m ON s.movie_id=m.id JOIN pddou_movie_cinema mc ON s.cinema_id=mc.cinema_id AND s.movie_id=mc.movie_id WHERE s.cinema_id=#{cinemaId}
AND m.`name` LIKE CONCAT('%',#{movieName},'%')
AND s.hall_name LIKE CONCAT('%',#{hallName},'%')
AND m.`name` LIKE CONCAT('%',#{movieName},'%')
AND CONCAT(s.show_date,' ',s.show_time) > #{now}
AND #{now} > CONCAT(s.show_date,' ',s.show_time)

SELECT s.id, m.`name` AS movie_name, s.hall_name, s.show_date, s.show_time, mc.price FROM pddou_schedule s JOIN pddou_movie m ON s.movie_id=m.id JOIN pddou_movie_cinema mc ON s.cinema_id=mc.cinema_id AND s.movie_id=mc.movie_id WHERE s.cinema_id=#{cinemaId}
AND m.`name` LIKE CONCAT('%',#{movieName},'%')
AND s.hall_name LIKE CONCAT('%',#{hallName},'%')
AND m.`name` LIKE CONCAT('%',#{movieName},'%')
AND CONCAT(s.show_date,' ',s.show_time) > #{now}
AND #{now} > CONCAT(s.show_date,' ',s.show_time)
ORDER BY s.show_date LIMIT #{page}, #{limit}
